import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserDetails = ({ userId }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchUserDetails = async () => {
      setLoading(true); // Set loading to true when fetching starts
      try {
        const response = await axios.get(`http://localhost:8000/api/user/${userId}`);
        setUserDetails(response.data);
      } catch (error) {
        console.error('Error fetching user details:', error);
      } finally {
        setLoading(false); // Set loading to false when fetching ends (success or error)
      }
    };

    if (userId) {
      fetchUserDetails();
    }
  }, [userId]);

  return (
    <div>
      {loading ? ( // Show loading message if loading is true
        <p>Loading user details...</p>
      ) : userDetails ? ( // Render user details if userDetails is not null
        <div>
          <h2>User Details</h2>
          <p>First Name: {userDetails.fname}</p>
          <p>Last Name: {userDetails.lname}</p>
          <p>Phone Number: {userDetails.phone}</p>
          <p>Email: {userDetails.email}</p>
          <p>Category: {userDetails.category}</p>
        </div>
      ) : ( // Show message if userDetails is null (user not found)
        <p>User not found.</p>
      )}
    </div>
  );
};

export default UserDetails;
